"use strict";var utl=require("../helpers").utl;var daysOf={week:function week(){var now=new Date(this.d), monday=utl.startOfWeek(now), days=[];for(var i=0; i <= 6; i++) {days.push(new Date(monday).setDate(monday.getDate() + i));}return days;}, month:function month(){var now=new Date(this.d), monthStart=utl.startOfMonth(now), monthEnd=utl.endOfMonth(now), diff=utl.daysBetween(monthStart, monthEnd), days=[];for(var i=0; i <= diff; i++) {days.push(new Date(monthStart).setDate(monthStart.getDate() + i));}return days;}, calendarMonth:function calendarMonth(){var now=new Date(this.d), monthStart=utl.startOfMonth(now), monthEnd=utl.endOfMonth(now), firstMonday=utl.startOfWeek(monthStart), lastSunday=utl.endOfWeek(monthEnd), diff=utl.daysBetween(firstMonday, lastSunday), days=[];for(var i=0; i <= diff; i++) {days.push(new Date(firstMonday).setDate(firstMonday.getDate() + i));}return days;}};daysOf.w = daysOf.week;daysOf.m = daysOf.month;daysOf.cm = daysOf.calendarMonth;module.exports = daysOf;